import styled from "styled-components";
import { shade } from 'polished';

export const ArtBrowser = styled.div`
    position: absolute;
    z-index: 1;
    right: 0;
    bottom: 0;
    display: flex;
    flex-direction: column;
    width: 400px;
`;

export const ArtBrowserWindowBar = styled.div`
    display: flex;
    align-items: center;
    height: 25px;
    border-top-left-radius: 5px;
    background: ${props => props.theme.colors.background && shade(0.05, props.theme.colors.background)};
    padding: 10px;
`;

export const ArtBrowserCloseButton = styled.div`
    width: 15px;
    height: 15px;
    border-radius: 7.5px;
    background: #E74A4B; 
    cursor: pointer;
`;

export const ArtBrowserMinimizeButton = styled.div`
    width: 15px;
    height: 15px;
    border-radius: 7.5px;
    background: #E7C51E; 
    margin: 0 5px;
    cursor: pointer;
`;

export const ArtBrowserMaximizeButton = styled.div`
    width: 15px;
    height: 15px;
    border-radius: 7.5px;
    background: #72BF43; 
    cursor: pointer;
`;

export const ArtBrowserContent = styled.div`
    display: flex;
    flex-direction: column;
    height: 300px;
    padding: 5px;
    background: ${props => props.theme.colors.background && shade(0.15, props.theme.colors.background)};
    font-size: 22px;    
`;
