import React, { useContext } from 'react';

import Switch from 'react-switch';
import { ThemeContext } from 'styled-components';
import { shade } from 'polished';

import { Container, Logo, SocialMedias } from './styles';

import PerfilLogo from './images/logo.png'
import GithubLogo from './images/icons/social-medias-github.svg';
import InstagramLogo from './images/icons/social-medias-instagram.svg';
import LinkedinLogo from './images/icons/social-medias-linkedin.svg';
import WhatsappLogo from './images/icons/social-medias-whatsapp.svg';

function Header({ toggleTheme }) {

    const { colors, title } = useContext(ThemeContext);

    return (
        <Container>
            <Logo src={PerfilLogo} /> Bryan Diniz

            <SocialMedias>
                <a href={'#'}><img src={GithubLogo} /></a>
                <a href={'#'}><img src={InstagramLogo} /></a>
                <a href={'#'}><img src={LinkedinLogo} /></a>
                <a href={'#'}><img src={WhatsappLogo} /></a>
            </SocialMedias>

            <Switch
                onChange={toggleTheme}
                checked={title === 'dark'}
                checkedIcon={false}
                uncheckedIcon={false}
                height={10}
                width={40}
                handleDiameter={20}
                offColor={colors.secondary}
                onColor={shade(0.15, colors.primary)}
            />

        </Container>
    );

}

export default Header;
