import styled from "styled-components";
import { shade, lighten } from "polished";

export const Project = styled.div`
    width: 100%;
    padding: 10px;
    border-radius: 5px;
    background: ${props => props.theme.colors.textBackground && shade(0.03, props.theme.colors.textBackground)};
    margin-top: 15px;
`;

export const ProjectTitle = styled.div`
    /* text-align: center; */
    font-size: 18px;
    font-weight: bold;
    text-transform: uppercase;
    margin-bottom: 15px;

    &:hover{
        color: ${props => props.theme.colors.textBackground && lighten(0.1, props.theme.colors.textBackground)};
    }

    @media (max-width: 560px) {
        font-size: 14px !important;
    }
`;

export const ProjectDescription = styled.div`
    padding: 10px;
    border-radius: 5px;
    text-align: justify;
    background: ${props => props.theme.colors.primary && lighten(0.06, props.theme.colors.primary)};
    margin-top: 15px;

    a{
        font-weight: bold;
    }
    a:hover{
        color: blue;
        cursor: pointer;
    }
`;

export const ProjectContainer = styled.div`
    display: flex;
    justify-content: space-around;
    align-items: center;
    margin-top: 15px;

    img{
        height: 530px;
        border-radius: 5px;
        margin-left: 15px;
    }

    @media (max-width: 980px) {
        font-size: 12px !important;

        img{
            display: none;
        }
    }
`;

export const CodeBlock = styled.pre`
    width: 100%;
    padding: 10px !important;
    margin: 0 !important;
    border-radius: 5px !important;
    font-family: Consolas;
    font-size: 16px;
    line-height: 1.5em;
    background: ${props => props.theme.colors.primary && lighten(0.06, props.theme.colors.primary)};
    overflow: auto;

    code{
        color: ${props => props.theme.colors.primary};
    }

    h3{
        font-family: sans-serif;;
    }

    @media (max-width: 560px) {
        font-size: 12px !important;
    }
`;
