import React, { useState, useContext } from 'react';

import { ThemeContext } from 'styled-components';
import { Container, ToggleButtonClosed, Item, ArrowRight, ToggleButtonOpened, ArrowLeft } from './styles';

import ButtonHome from '../Buttons/Home';
import ButtonContact from '../Buttons/Contact';
import ButtonProjects from '../Buttons/Projects';
import ButtonTechs from '../Buttons/Techs';

function SideBar({ toggleSideBar, isOpen }) {

    const { colors } = useContext(ThemeContext);

    if (!isOpen) {
        return (
            <Container id={'sidebar'} style={{ width: 60 }}>
                <ToggleButtonClosed onClick={toggleSideBar}>
                    <ArrowRight />
                </ToggleButtonClosed>
                <Item href={'/'}><ButtonHome color={colors.text} /></Item>
                <Item href={'/contact'}><ButtonContact color={colors.text} /></Item>
                <Item href={'/project'}><ButtonProjects color={colors.text} /></Item>
                <Item href={'/techs'}><ButtonTechs color={colors.text} /></Item>
            </Container>
        );
    } else {
        return (
            <Container id={'sidebar'} style={{ width: 145, left: 0, alignItems: 'left' }}>
                <ToggleButtonOpened onClick={toggleSideBar}>
                    <ArrowLeft />
                </ToggleButtonOpened>
                <Item href={'/'}><h4><ButtonHome color={colors.text} />&nbsp;HOME</h4></Item>
                <Item href={'/contact'}><h4><ButtonContact color={colors.text} />&nbsp;CONTATO</h4></Item>
                <Item href={'/project'}><h4><ButtonProjects color={colors.text} />&nbsp;PROJETOS</h4></Item>
                <Item href={'/techs'}><h4><ButtonTechs color={colors.text} />&nbsp;TECHS</h4></Item>
            </Container>
        );
    }
}

export default SideBar;
