import styled from "styled-components";
import { shade, lighten } from 'polished';

export const Container = styled.div`
    transition-property: width, left; 
    transition-duration: .7s; 
    position: fixed;
    display: flex;
    flex-direction: column;
    justify-content: start;
    align-self: center;

    padding-top: 15px;
    top: 50px;
    left: 0;   
    width: 60px;
    min-height: calc(100vh - 50px);

    background: #343746; 
    color: ${props => props.theme.colors.textPrimary};    

    a{
        display: flex;
        align-items: center;
        text-decoration: none;
        color: ${props => props.theme.colors.textPrimary};
        margin: 0;
        padding: 0;
        height: 50px;
        padding-left: 10px;
    }

    a:hover{
        background: ${props => props.theme.colors.primary && shade(0.15, props.theme.colors.primary)};
    }

    h4{
        font-size: 14px;
        line-height: 30px;     
        display: flex;
        align-items: center; 
        margin:0;
        padding: 0;
    }

    svg{
        flex-shrink: 0;
        margin-right: 10px;
    }

    a:hover path,
    a:hover circle,
    a:hover{
        fill: ${props => props.theme.colors.textPrimary && lighten(0.2, props.theme.colors.textPrimary)};
        color: ${props => props.theme.colors.textPrimary && lighten(0.2, props.theme.colors.textPrimary)};
    }

    @media (max-width: 560px) {
        left: -60px;
    }

    @media print {
        display: none;
    }
`;

export const ContainerExternal = styled.div`
    @media (max-width: 935px) {
        width: calc(100% - 155px);
        height: 100vh;
        position: fixed;
        z-index: -20;
        top: 0;
        right: 0;
    }
`;
