import React, { useState, useEffect, useContext, useRef } from 'react';
import io from "socket.io-client";

import { Container, PageTitle, MessageContainer, Message, MessageUser, MessageText, MessageDate, InputGroup, InputText, LabelInput, InputMessage, SendButtonInput, InputTextBox, Button } from './styles';

const socket = io('http://localhost:3333', {});
socket.on('connect', () => { console.log('[IO] Connect => Connection established') });
socket.on('recivedMessage', m => {
    alert('AAA');
    //setMessages(m);
});

function Chat() {

    const [messages, setMessages] = useState([]);
    const messageText = useRef();
    const username = useRef();

    /*
    useEffect(() => {
        const socket = io('http://localhost:3333', {});
        socket.on('connect', () => { console.log('[IO] Connect => Connection established') });

    }, []);
    */




    async function handleSubmit(event) {
        event.preventDefault();

        const date = new Date().toTimeString().replace(/.*(\d{2}:\d{2}:\d{2}).*/, "$1");;

        const newMessage = {
            id: 1,
            username: username.current.value,
            messageText: messageText.current.value,
            date,
        }

        socket.emit('sendMessage', newMessage);

        await setMessages([...messages, newMessage]);

        console.log(messages);
    }

    return (
        <Container onSubmit={handleSubmit}>
            {/* <PageTitle>Chat websocket</PageTitle> */}

            <InputGroup>
                <LabelInput htmlFor="username">Username</LabelInput>

                <InputText
                    id="username"
                    ref={username}
                    required />
            </InputGroup>

            <MessageContainer>

                {messages.map(m => (
                    <Message key={m.id}>
                        <MessageText>
                            <MessageUser>{m.username}</MessageUser>
                            {m.messageText}
                        </MessageText>
                        <MessageDate>{m.date}</MessageDate>
                    </Message>
                ))}

                {/* <Message>
                    <MessageText>
                        <MessageUser>User02:</MessageUser>
                        Ut sodales sem eu elementum tristique. Curabitur ligula sem, imperdiet sit amet arcu eget,
                        faucibus luctus purus. Vivamus justo urna, imperdiet ut elit vel, maximus placerat sapien.
                    </MessageText>
                    <MessageDate>21:00</MessageDate>
                </Message>

                <Message>
                    <MessageText>
                        <MessageUser>User03:</MessageUser>
                        Mauris viverra facilisis mi, elementum tincidunt lectus volutpat non. Etiam viverra ex turpis,
                        in fermentum nibh mattis scelerisque. Duis nec congue justo.
                    </MessageText>
                    <MessageDate>22:00</MessageDate>
                </Message> */}
            </MessageContainer>

            <InputGroup>
                <InputMessage
                    id="message"
                    placeholder="Enviar mensagem..."
                    ref={messageText}
                    required />

                <SendButtonInput>Enviar</SendButtonInput>
            </InputGroup>

        </Container>
    );
}

export default Chat;
