    border-radius: 5px;
    margin-bottom: 15px;
    padding: 15px;
    background: ${props => props.theme.colors.background && lighten(0.03, props.theme.colors.background)};
    box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.2);
`;

export const Title = styled.div`
    margin-bottom: 15px;
    font-size: 16px;
    font-weight: bold;
`;

export const Subtitle = styled.div`
    margin-bottom: 5px;
    font-size: 14px;
    font-weight: bold;
`;

export const Line = styled.div`
    width: 100%;
    height: 1px;
    margin: 7.5px 0;
