    margin-bottom: 15px;
    border: none;
    border-radius: 5px;
    outline: none;
    background: ${props => props.theme.colors.primary && lighten(0.03, props.theme.colors.primary)};
    color: ${props => props.theme.colors.text};
    font-size: 16px;
    cursor: pointer;

    &:hover{
        background: ${props => props.theme.colors.primary};
    }

`;
