import React, { useContext } from 'react';

import Switch from 'react-switch';
import { ThemeContext } from 'styled-components';
import { shade } from 'polished';

import { Container, Logo, Name, SocialMedias } from './styles';

import PerfilLogo from './images/logo.png'
import GithubButton from '../Buttons/GitHub';
import InstagramButton from '../Buttons/Instagram';
import LinkedinButton from '../Buttons/Linkedin';
import WhatsappButton from '../Buttons/Whatsapp';

function Header({ toggleTheme }) {

    function newTab(url){
        window.open(url);
    }

    const { colors, title } = useContext(ThemeContext);

    return (
        <Container>
            <Logo src={PerfilLogo} /> <Name>Bryan Diniz</Name>

            <SocialMedias>
                <a onClick={() => newTab('www.google.com')}><GithubButton color={colors.text} /></a>
                <a href={'/#'}><InstagramButton color={colors.text} /></a>
                <a href={'/#'}><LinkedinButton color={colors.text} /></a>
                <a href={'/#'}><WhatsappButton color={colors.text} /></a>
            </SocialMedias>

            <Switch
                onChange={toggleTheme}
                checked={title === 'dark'}
                checkedIcon={false}
                uncheckedIcon={false}
                height={10}
                width={40}
                handleDiameter={20}
                offColor={shade(0.15, colors.primary)}
                onColor={shade(0.15, colors.primary)}
            />

        </Container>
    );

}

export default Header;
