import { createGlobalStyle } from 'styled-components';

export default createGlobalStyle`
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;  
}

body{
    transition: background-color .3s;
    background: ${props => props.theme.colors.background};
    font-family: sans-serif;
}
`;
