import React from 'react';
import chatScreenshot from './img/screenshot.jpg';

import { Link } from 'react-router-dom';

import { Project, ProjectTitle, ProjectDescription, ProjectContainer, CodeBlock } from './styles';

function ChatDescription() {

    function newTab(url) {
        window.open(url, '_blank');
    }

    return (

            <Project>
                <Link to={'/projects/chat'}>
                    <ProjectTitle>Chat com websockets</ProjectTitle>             
                </Link>

                <ProjectDescription>
                    Projeto de um chat em tempo real feito utilizando Node, Express e React com a biblioteca socket.io tanto no Client-Side
                    quanto no Server-side. O backend e o frontend são relativamente simples, como podemos ver abaixo:
                </ProjectDescription>

                <ProjectContainer>
                    <CodeBlock>
                        <h3>Backend NodeJS</h3>

                        <code className="language-javascript">
                            {
                                `const express = require('express')
const http = require('http');

const app = express();
const server = http.createServer(app);
const io = require('socket.io').listen(server);

let messageId = 0;

io.on('connection', socket => {
    console.log('Socket conectado ' + socket.id);

    socket.on('sendMessage', data => {
        data.id = messageId++;
        socket.broadcast.emit('recivedMessage', data);
        console.log(data);
    });
});

server.listen(process.env.PORT || 3333);`
                            } </code>

                    </CodeBlock>
                    <img src={chatScreenshot} />
                </ProjectContainer>

                <ProjectDescription>
                    A comunicação do frontend com o backend é feito com o 
                    <a onClick={() => newTab('https://www.npmjs.com/package/socket.io-client')}> socket.io-client</a>
                </ProjectDescription>


                <ProjectDescription>
                    O código completo pode ser encontrado no meu repositório do github 
                    <a onClick={() => newTab('https://www.npmjs.com/package/socket.io-client')}> clicando aqui</a>.
                </ProjectDescription>

            </Project>
    );
}

export default ChatDescription;
