import styled from "styled-components";
import { shade, lighten } from 'polished';

export const Container = styled.div`
    position: absolute;
    display: flex;
    align-items: center;

    left: 0;   
    top: 15%;
    width: 60px;
    height: 80%;

    border-top-right-radius: 10px;
    border-bottom-right-radius: 10px;

    background: ${props => props.theme.colors.primary}; 
    color: ${props => props.theme.colors.text};    
`;

export const ToggleButton = styled.button`
    position: absolute;
    top: 5px;
    right: 5px;
    width: 20px;
    height: 20px;
    background: ${props => props.theme.colors.background && shade(0.05, props.theme.colors.background)};
    outline: none;
    border: none;
    border-radius: 10px;
    cursor: pointer;

    ${ToggleButton}:hover{
        ${props => props.theme.colors.background && shade(0.2, props.theme.colors.background)};
    }
`;
