import React, { useState } from 'react';
import { Switch, Route, Link } from 'react-router-dom';

import { ThemeProvider } from 'styled-components';
import light from './styles/themes/light'
import dark from './styles/themes/dark'
import userPersistedState from './utils/userPersistedState';
import GlobalStyle from './styles/global'

import Header from './components/Header';
import SideBar from './components/SideBar';
import Home from './components/Home';
import Contact from './components/Contact';

function App() {

  const [theme, setTheme] = userPersistedState('theme', light);
  const [sideBarToggle, setSideBarToggle] = useState(false);

  const toggleTheme = () => {
    setTheme(theme.title === 'light' ? dark : light);
  };

  const toggleSideBar = () => {
    setSideBarToggle(!sideBarToggle);
  }

  return (
    <ThemeProvider theme={theme}>
      <GlobalStyle />
      <Header toggleTheme={toggleTheme} />
      <SideBar toggleSideBar={toggleSideBar} isOpen={sideBarToggle} />

      <Switch>
        <Route exact path="/" component={Home} />
        <Route exact path="/contact" render={props => <Contact theme={theme} />} />
        {/* <Route exact path="/" render={props => <Home />} />
        <Route exact path="/contact" render={props => <Contact />} /> */}
      </Switch>

    </ThemeProvider>
  );
}

export default App;
