import styled from "styled-components";
import { shade, lighten } from "polished";

export const Container = styled.form`
    display: flex;
    flex-direction: column;
    justify-content: start;
    align-items: center;
    min-height: calc(100vh - 50px);
    width: 80%;
    margin: auto;
    padding: 10px;

    a{
        text-decoration: none;
        color: ${props => props.theme.colors.textBackground};
    }

    @media (max-width: 935px) {
        width: 75%;
    }

    @media (max-width: 560px) {
        width: 100%;
        padding: 5px;
    }
`;

export const PageTitle = styled.h1`
    font-size: 32px;
    text-transform: uppercase;
    text-align: center;
    margin-bottom: 15px;

  @media (max-width: 768px) {
        font-size: 24px;
  }

  @media (max-width: 560px) {
        font-size: 20px;
  }
`;

export const Project = styled.div`
    width: 100%;
    padding: 10px;
    border-radius: 5px;
    background: ${props => props.theme.colors.primary && lighten(0.03, props.theme.colors.primary)};
`;

export const ProjectTitle = styled.div`
    text-align: center;
    font-size: 18px;
    font-weight: bold;
    text-transform: uppercase;
    margin-bottom: 15px;

    &:hover{
        color: ${props => props.theme.colors.textBackground && lighten(0.1, props.theme.colors.textBackground)};
    }

    @media (max-width: 560px) {
        font-size: 14px !important;
    }
`;

export const ProjectDescription = styled.div`
    padding: 10px;
    border-radius: 5px;
    text-align: justify;
    background: ${props => props.theme.colors.primary && lighten(0.06, props.theme.colors.primary)};
`;

export const ProjectContainer = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 5px;

    img{
