    @media (max-width: 1300px) {
        display: none;
  }
`;

export const ArtBrowserWindowBar = styled.div`
    display: flex;
    align-items: center;
    height: 25px;
    border-top-left-radius: 5px;
    background: ${props => props.theme.colors.primary && lighten(0.1, props.theme.colors.primary)};
    padding: 10px;
`;

export const ArtBrowserCloseButton = styled.div`
    width: 15px;
    height: 15px;
    border-radius: 7.5px;
    background: #E74A4B; 
    cursor: pointer;
`;

export const ArtBrowserMinimizeButton = styled.div`
    width: 15px;
    height: 15px;
    border-radius: 7.5px;
    background: #E7C51E; 
    margin: 0 5px;
    cursor: pointer;
`;

export const ArtBrowserMaximizeButton = styled.div`
    width: 15px;
    height: 15px;
    border-radius: 7.5px;
    background: #72BF43; 
    cursor: pointer;
`;

export const ArtBrowserContent = styled.div`
    display: flex;
    flex-direction: column;
    height: 300px;
    border-bottom-left-radius: 5px;
    padding: 5px;
    background: ${props => props.theme.colors.primary && lighten(0.03, props.theme.colors.primary)};
    font-size: 18px;    
`;
