import styled from "styled-components";
import { shade } from 'polished';

export const Container = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

    padding: 0 50px;    
`;

export const Title = styled.h1`
    font-size: 32px;
    margin-top: 25px;
`;

export const ContainerPhoto = styled.div`
    display: flex;
    justify-content: center;
    align-items: baseline;
`;

export const PerfilPhoto = styled.img`
    width: 180px;
    height: 180px;
    border-radius: 90px;
    margin: 35px 20px;
`;

export const ContainerTechs = styled.div`
    display: flex;
    justify-content: center;
`;
export const MiddleLine = styled.div`
    width: 2px;
    background:${props => props.theme.colors.text};
    margin: 0 10px;
`;

export const ContainerTechsLeft = styled.div`
    display: flex;
    flex-direction: column;
    text-align: right;
`;

export const ContainerTechsRight = styled.div`
    display: flex;
    flex-direction: column;
`;

export const ArtBrowser = styled.div`
    display: flex;
    flex-direction: column;
    width: 400px;
`;

export const ArtBrowserWindowBar = styled.div`
    display: flex;
    align-items: center;
    height: 25px;
    border-top-left-radius: 5px;
    background: ${props => props.theme.colors.primary}; 
    padding: 5px;
`;

export const ArtBrowserCloseButton = styled.div`
    width: 15px;
    height: 15px;
    border-radius: 7.5px;
    background: #E74A4B; 
`;

export const ArtBrowserMinimizeButton = styled.div`
    width: 15px;
    height: 15px;
    border-radius: 7.5px;
    background: #E7C51E; 
    margin: 0 5px;
`;

export const ArtBrowserMaximizeButton = styled.div`
    width: 15px;
    height: 15px;
    border-radius: 7.5px;
    background: #72BF43; 
`;

export const ArtBrowserContent = styled.div`
    display: flex;
    flex-direction: column;
    height: 300px;
    background: ${shade(0.15, props => props.theme.colors.primary)}; 
`;
