import styled from "styled-components";

export const Container = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: space-around;
    align-items: center;
    border: 1px solid red;
`;

export const ContainerLeft = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

    border: 1px solid red;
`;

export const Title = styled.h1`
    font-size: 28px;
    margin-top: 25px;


  @media (max-width: 768px) {
        font-size: 24px;
  }

  @media (max-width: 560px) {
        font-size: 20px;
  }

  @media (max-width: 460px) {
    margin-left: 15px;
    margin-right: 15px;
    margin-bottom: 15px;
  }
`;

export const ContainerPhoto = styled.div`
    display: flex;
    justify-content: center;
    align-items: baseline;
`;

export const PerfilPhoto = styled.img`
    width: 180px;
    height: 180px;
    border-radius: 90px;
    margin: 35px 20px;

    @media (max-width: 768px) {
        width: 130px;
        height: 130px;
        margin: 30px 5px;
  }

  @media (max-width: 560px) {
        width: 100px;
        height: 100px;
        margin: 30px 5px;
  }

  @media (max-width: 460px) {
        display: none;
  }
`;

export const ContainerTechs = styled.div`
    display: flex;
    justify-content: center;

    @media (max-width: 768px) {
        font-size: 14px;
    }
    @media (max-width: 560px) {
        font-size: 12px;
    }
`;
export const MiddleLine = styled.div`
    width: 2px;
    background:${props => props.theme.colors.text};
    margin: 0 10px;
`;

export const ContainerTechsLeft = styled.div`
    display: flex;
    flex-direction: column;
    text-align: right;
`;

export const ContainerTechsRight = styled.div`
    display: flex;
    flex-direction: column;
`;
