import React, { useEffect } from 'react';

import {
    ArtBrowser, ArtBrowserWindowBar,
    ArtBrowserCloseButton, ArtBrowserMinimizeButton,
    ArtBrowserMaximizeButton, ArtBrowserContent
} from './styles';

function Browser() {

    const lines = Array('<!DOCTYPE html>', '<html>', '<head>', '<title>Seja bem vindo</title>',
        '</head>', '<body>', '<p>Olá como vai? Me chamo Bryan, tenho 19 anos e sou de Minas Gerais</p>',
        '<p>Estudo programação já faz algum tempo, e é algo que eu realmente gosto :)', '</body>', '</html>');

    useEffect(() => {
        let screen = document.getElementById('screen');

        for (const i in lines) {
            let paragraph = document.createElement("p");
            let chars = lines[i].split('');

            for (const j in chars) {
                paragraph.innerHTML += chars[j];
            }     

            //screen.append(paragraph);
        }


        lines.forEach((line, i) => {
            setTimeout(() => {
                let parent = document.createElement("p");

                let chars = line.split('');

                chars.forEach((char, j) => {
                    setTimeout(() => {
                        parent.innerHTML += char;
                    }, j * 100);
                });


                screen.append(parent);

            }, i * 1000);
        });

    }, []);

    return (
        <ArtBrowser>
            <ArtBrowserWindowBar>
                <ArtBrowserCloseButton />
                <ArtBrowserMinimizeButton />
                <ArtBrowserMaximizeButton />
            </ArtBrowserWindowBar>
            <ArtBrowserContent id={'screen'}>


                {/* {lines.map(line => (
                    <p>{line}</p>
                ))} */}

                {/* <p>{'<!DOCTYPE html>'}</p>
                <p>{'<html>'}</p>
                <p>{'<head>'}</p>
                <p>{'<title>Seja bem vindo</title>'}</p>
                <p>{'</head>'}</p>
                <p>{'<body>'}</p>
                <p>{'<p>Olá como vai? Me chamo Bryan, tenho 19 anos e sou de Minas Gerais</p>'}</p>
                <p>{'<p>Estudo programação já faz algum tempo, e é algo que eu realmente gosto :)'}</p>
                <p>{'</body>'}</p>
                <p>{'</html>'}</p> */}
            </ArtBrowserContent>
        </ArtBrowser>
    );
}

export default Browser;
