import React, { useEffect } from 'react';

import CloseIcon from './icons/close';

import {
    ArtBrowser, ArtBrowserWindowBar,
    ArtBrowserCloseButton, ArtBrowserMinimizeButton,
    ArtBrowserMaximizeButton, ArtBrowserContent
} from './styles';

function Browser() {

    const text = 'Meu nome é Bryan Diniz, teho 19 anos, moro em Minas Gerais. Estudo programação já faz algum tempo, e é algo que eu realmente gosto :)';

    useEffect(() => {
        let screen = document.getElementById('screen');

        let paragraph = document.createElement("p");
        paragraph.innerHTML = 'root:~$ ';
        screen.append(paragraph);

        let chars = text.split('');

        chars.forEach((char, i) => {
            setTimeout(() => {
                paragraph.innerHTML += char;
            }, i * 50);
        });

    }, []);

    return (
        <ArtBrowser>
            <ArtBrowserWindowBar>
                <ArtBrowserCloseButton><CloseIcon/></ArtBrowserCloseButton>
                <ArtBrowserMinimizeButton />
                <ArtBrowserMaximizeButton />
            </ArtBrowserWindowBar>
            <ArtBrowserContent id={'screen'}>
            </ArtBrowserContent>
        </ArtBrowser>
    );
}

export default Browser;
