import React, { useState, useContext, useRef } from 'react';
import Prism from "prismjs";
import chatScreenshot from './Chat/img/screenshot.jpg';

import { Link } from 'react-router-dom';

import { Container, Project, PageTitle, ProjectTitle, ProjectDescription, ProjectContainer, CodeBlock } from './styles';

function Projects() {

    Prism.highlightAll();

    return (
        <Container>
            <PageTitle>Projetos</PageTitle>

            <Project>
                <Link to={'/projects/chat'}><ProjectTitle>Chat em tempo real com websockets</ProjectTitle></Link>

                <ProjectDescription>
                    Projeto de um chat em tempo real feito utilizando Node, Express e React com a biblioteca socket.io tanto no Client-Side
                    quanto no Server-side. O backend e o frontend são relativamente simples, como podemos ver abaixo:
                </ProjectDescription>

                <ProjectContainer>
                    <CodeBlock>
                        <h3>Backend NodeJS</h3>

                        <code className="language-javascript">
                            {
                                `const express = require('express')
const http = require('http');

const app = express();
const server = http.createServer(app);
const io = require('socket.io').listen(server);

let messageId = 0;

io.on('connection', socket => {
    console.log('Socket conectado ' + socket.id);

    socket.on('sendMessage', data => {
        data.id = messageId++;
        socket.broadcast.emit('recivedMessage', data);
        console.log(data);
    });
});

server.listen(process.env.PORT || 3333);`
                            } </code>

                    </CodeBlock>
                    <img src={chatScreenshot} />
                </ProjectContainer>


                <ProjectDescription>
                    O restante do código pode ser encontrado no meu repositório do github <a href="/#">clicando aqui</a>.
                </ProjectDescription>

            </Project>


        </Container>
    );
}

export default Projects;
