import { createGlobalStyle } from 'styled-components';
import { shade, lighten } from 'polished'


export default createGlobalStyle`
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;  
}

body{
    background: ${props => props.theme.colors.background};
    font-family: sans-serif;
    color: ${props => props.theme.colors.textBackground};
}
`;

export const InputGroup = styled.div`
    width: 100%;
    display: flex;
    flex-direction: row;
    margin-bottom: 15px;
`;

export const InputText = styled.input`
    width: calc(100% - 100px);
    height: 40px;
    border: none;
    border-top-right-radius: 5px;
    border-bottom-right-radius: 5px;
    background: ${props => props.theme.colors.primary && lighten(0.15, props.theme.colors.primary)};
    text-indent: 10px;
    font-size: 16px;
    color: ${props => props.theme.colors.textBackground};
`;

export const LabelInput = styled.label`
    display: flex;
    align-items: center;
    justify-content: center;
    height: 40px;
    width: 100px;
    border-top-left-radius: 5px;
    border-bottom-left-radius: 5px;
    background: ${props => props.theme.colors.primary && lighten(0.2, props.theme.colors.primary)};
    color: ${props => props.theme.colors.textBackground};
    cursor: pointer;
`;

export const InputTextBox = styled.textarea`
    width: 100%;
    height: 180px;
    margin-bottom: 15px;
    resize: none;
    border: none;
    border-radius: 5px;
    background: ${props => props.theme.colors.primary && lighten(0.15, props.theme.colors.primary)};
    padding: 10px;
    font-size: 16px;
    color: ${props => props.theme.colors.textBackground};
`;

export const Button = styled.button`
    width: 140px;
    height: 40px;
    margin-bottom: 15px;
    border: none;
    border-radius: 5px;
    outline: none;
    background: ${props => props.theme.colors.primary && lighten(0.03, props.theme.colors.primary)};
    color: ${props => props.theme.colors.textPrimary};
    font-size: 16px;
    cursor: pointer;

    &:hover{
        background: ${props => props.theme.colors.primary};
    }

`;
