import styled from "styled-components";
import { shade, lighten } from 'polished';

export const ArtBrowser = styled.div`
    display: flex;
    flex-direction: column;
    width: 400px;
    align-self: flex-end;
    margin-top: 20px;
    box-shadow: 0px 0px 5px 3px rgba(0,0,0,0.2);

    @media (max-width: 465px) {
        width: 70%;       
  } 
`;

export const ArtBrowserWindowBar = styled.div`
    display: flex;
    align-items: center;
    height: 25px;
    border-top-left-radius: 5px;
    background: ${props => props.theme.colors.background && lighten(0.02, props.theme.colors.background)};
    padding: 10px;
`;

export const ArtBrowserCloseButton = styled.div`
    width: 15px;
    height: 15px;
    border-radius: 7.5px;
    background: #E74A4B; 
    cursor: pointer;
`;

export const ArtBrowserMinimizeButton = styled.div`
    width: 15px;
    height: 15px;
    border-radius: 7.5px;
    background: #E7C51E; 
    margin: 0 5px;
    cursor: pointer;
`;

export const ArtBrowserMaximizeButton = styled.div`
    width: 15px;
    height: 15px;
    border-radius: 7.5px;
    background: #72BF43; 
    cursor: pointer;
`;

export const ArtBrowserContent = styled.div`
    display: flex;
    flex-direction: column;
    height: 200px;
    padding: 5px;
    background: ${props => props.theme.colors.background && shade(0.05, props.theme.colors.background)};
    font-size: 16px;
    color: ${props => props.theme.colors.textBackground};

    p::after{
        content: '|';
        margin-left: 5px;
        opacity: 1;
        animation: pisca .7s infinite;
    } 

    @keyframes pisca{
        0%, 100%{
            opacity: 1;
        }
        50%{
            opacity: 0;
        }
    }
`;
