import React, { useContext } from 'react';
import { Link } from 'react-router-dom';

import { ThemeContext } from 'styled-components';
import { Container, ContainerExternal } from './styles';

import ButtonHome from '../Buttons/Home';
import ButtonContact from '../Buttons/Contact';
import ButtonProjects from '../Buttons/Projects';
import ButtonTechs from '../Buttons/Techs';
import ButtonDocument from '../Buttons/Document';

function SideBar({ toggleSideBar, isOpen }) {

    const { colors } = useContext(ThemeContext);

    if (!isOpen) {
        return (
            <Container id={'sidebar'} style={{ width: 50 }}>
                <Link to={'/'}><ButtonHome color={colors.textPrimary} /></Link>
                <Link to={'/contact'}><ButtonContact color={colors.textPrimary} /></Link>
                <Link to={'/projects'}><ButtonProjects color={colors.textPrimary} /></Link>
                <Link to={'/techs'}><ButtonTechs color={colors.textPrimary} /></Link>
                <Link to={'/curriculum'}><ButtonDocument color={colors.textPrimary} /></Link>
            </Container>
        );
    } else {
        return (
            <Container id={'sidebar'} style={{ width: 155, left: 0, overflow: 'hidden' }}>
                <Link to={'/'}><h4><ButtonHome color={colors.textPrimary} />HOME</h4></Link>
                <Link to={'/contact'}><h4><ButtonContact color={colors.textPrimary} />CONTATO</h4></Link>
                <Link to={'/projects'}><h4><ButtonProjects color={colors.textPrimary} />PROJETOS</h4></Link>
                <Link to={'/techs'}><h4><ButtonTechs color={colors.textPrimary} />TECHS</h4></Link>
                <Link to={'/curriculum'}><h4><ButtonDocument color={colors.textPrimary} />CURRÍCULO</h4></Link>
                <ContainerExternal onClick={toggleSideBar}/>
            </Container>
        );
    }
}

export default SideBar;
