import React, { useState, useContext, useRef } from 'react';
import Prism from "prismjs";
import "./prism.css";

import { Link } from 'react-router-dom';

import { Container, Project, PageTitle, ProjectTitle, ProjectDescription, CodeBlock, CodeLine } from './styles';

function Projects() {

    return (
        <Container>
            <PageTitle>Projetos</PageTitle>

            <Project>
                <Link to={'/projects/chat'}><ProjectTitle>Chat em tempo real com websockets</ProjectTitle></Link>

                <ProjectDescription>
                    Projeto de um chat em tempo real feito utilizando Node, Express e React com a biblioteca socket.io tanto no Client-Side
                    quanto no Server-side. Tanto o backend quanto o frontend são simples, como podemos ver abaixo:
                </ProjectDescription>

                <CodeBlock className="brush: python">
                    <h3>Backend NodeJS</h3>

                    <code className="language-javascript">
                        {
`const express = require('express')
const http = require('http');

const app = express();
const server = http.createServer(app);
const io = require('socket.io').listen(server);

let messageId = 0;

io.on('connection', socket => {
    console.log('Socket conectado ' + socket.id);

    socket.on('sendMessage', data => {
        data.id = messageId++;
        socket.broadcast.emit('recivedMessage', data);
        console.log(data);
    });
});

server.listen(process.env.PORT || 3333);`
                        } </code>

                </CodeBlock>

                <CodeBlock className="brush: python">
                    <h3>Front ReactJS</h3>

                    <code className="language-javascript">
                        {
`import React, { useState, useEffect, useRef, useMemo } from 'react';

import socket from "../../../services/socket";

import { Container, PageTitle, MessageContainer, Message, MessageUser, MessageText, MessageDate, InputGroup, InputText, LabelInput, InputMessage, SendButtonInput, InputTextBox, Button } from './styles';

function Chat() {
    const [messages, setMessages] = useState([]);
    const [recivedMessage, setRecivedMessage] = useState(null);

    const messageText = useRef();
    const username = useRef();

    useMemo(() => { 
        socket.on('recivedMessage', message => {
            setRecivedMessage(message);
            console.log(message);
        })
    }, []);

    useEffect(()=> {
        if(recivedMessage) setMessages([...messages, recivedMessage]);
    },[recivedMessage]);

    function handleSubmit(event) {
        event.preventDefault();

        const date = new Date().toTimeString().replace(/.*(\d{2}:\d{2}:\d{2}).*/, "$1");;

        const newMessage = {
            id: 0,
            username: username.current.value,
            messageText: messageText.current.value,
            date,
        }

        socket.emit('sendMessage', newMessage);

        setMessages([...messages, newMessage]);

        messageText.current.value = '';
    }

    return (
        <Container autoComplete="off" onSubmit={handleSubmit}>

            <InputGroup>
                <LabelInput htmlFor="username">Username</LabelInput>

                <InputText
                    id="username"
                    ref={username}
                    required />
            </InputGroup>

            <MessageContainer id="messageContainer">

                {messages.map(m => (
                    <Message key={m.id}>
                        <MessageText>
                            <MessageUser>{m.username}</MessageUser>
                            {m.messageText}
                        </MessageText>
                        <MessageDate>{m.date}</MessageDate>
                    </Message>
                ))}

            </MessageContainer>

            <InputGroup>
                <InputMessage
                    id="message"
                    placeholder="Enviar mensagem..."
                    ref={messageText}
                    required />

                <SendButtonInput>Enviar</SendButtonInput>
            </InputGroup>

        </Container>
    );
}

export default Chat;`
                        } </code>

                </CodeBlock>

            </Project>


        </Container>
    );
}

export default Projects;
