import React, { useState, useContext } from 'react';
import MaskedInput from 'react-text-mask';

import {
    Container, Title, InputGroup, InputText, LabelInput, InputTextBox, Button
} from './styles';


function Contact() {

    const [name, setName] = useState('');
    const [phone, setPhone] = useState('');
    const [email, setEmail] = useState('');
    const [subject, setSubject] = useState('');


    async function handdleSubmit(e) {
        e.preventDefault();

        console.log(e.target.name.value);

    }

    return (
        <Container onSubmit={handdleSubmit}>

            <Title>Contato</Title>

            <InputGroup>
                <LabelInput htmlFor="name">Nome</LabelInput>

                <InputText
                    id="name"
                    name="name"
                    onBlur={e => setName(e.target.value)}
                    required />

            </InputGroup>

            <InputGroup>
                <LabelInput htmlFor={'phone'}>Telefone</LabelInput>

                <MaskedInput
                    className="MaskedInput"
                    id="phone"
                    mask={['(', /[1-9]/, /\d/, ')', ' ', /\d/, ' ', /\d/, /\d/, /\d/, /\d/, '-', /\d/, /\d/, /\d/, /\d/]}
                    placehoder="(00) 0 0000-0000"
                    guide={true}
                    onBlur={e => setPhone(e.target.value)}
                    required />

            </InputGroup>

            <InputGroup>
                <LabelInput htmlFor="email">E-mail</LabelInput>

                <InputText
                    id="email"
                    name="email"
                    type="email"
                    onBlur={e => setEmail(e.target.value)}
                    required />

            </InputGroup>

            <InputTextBox
                id="subject"
                onBlur={e => setSubject(e.target.value)}
                required />

            <Button>Enviar</Button>

        </Container>
    );
}

export default Contact;
