import React, { useContext } from 'react';
import { Link } from 'react-router-dom';

import { ThemeContext } from 'styled-components';
import { Container, ToggleButtonClosed, ArrowRight, ToggleButtonOpened, ArrowLeft } from './styles';

import ButtonHome from '../Buttons/Home';
import ButtonContact from '../Buttons/Contact';
import ButtonProjects from '../Buttons/Projects';
import ButtonTechs from '../Buttons/Techs';
import ButtonDocument from '../Buttons/Document';

function SideBar({ toggleSideBar, isOpen }) {

    const { colors } = useContext(ThemeContext);

    if (!isOpen) {
        return (
            <Container id={'sidebar'} style={{ width: 60 }}>
                <ToggleButtonClosed onClick={toggleSideBar}>
                    <ArrowRight />
                </ToggleButtonClosed>
                <Link to={'/'}><ButtonHome color={colors.text} /></Link>
                <Link to={'/contact'}><ButtonContact color={colors.text} /></Link>
                <Link to={'/project'}><ButtonProjects color={colors.text} /></Link>
                <Link to={'/techs'}><ButtonTechs color={colors.text} /></Link>
                <Link to={'/curriculum'}><ButtonDocument color={colors.text} /></Link>
            </Container>
        );
    } else {
        return (
            <Container id={'sidebar'} style={{ width: 145, left: 0, overflow: 'hidden' }}>
                <ToggleButtonOpened onClick={toggleSideBar}>
                    <ArrowLeft />
                </ToggleButtonOpened>
                <Link to={'/'}><h4><ButtonHome color={colors.text} />HOME</h4></Link>

                <Link to={'/contact'}><h4><ButtonContact color={colors.text} />CONTATO</h4></Link>
                <Link to={'/project'}><h4><ButtonProjects color={colors.text} />PROJETOS</h4></Link>
                <Link to={'/techs'}><h4><ButtonTechs color={colors.text} />TECHS</h4></Link>
                <Link to={'/curriculum'}><h4><ButtonDocument color={colors.text} />CURRÍCULO</h4></Link>
            </Container>
        );
    }
}

export default SideBar;
