import React, { useState } from 'react';
import { Switch, Route, Link } from 'react-router-dom';

import { ThemeProvider } from 'styled-components';
import light from './styles/themes/light'
import dark from './styles/themes/dark'
import userPersistedState from './utils/userPersistedState';
import GlobalStyle from './styles/global'

import Header from './components/Header';
import SideBar from './components/SideBar';

import Home from './pages/Home';
import Contact from './pages/Contact';
import Curriculum from './pages/Curriculum';
import Projects from './pages/Projects';
import Techs from './pages/Techs';

// --> Projects subpages
import Chat from './pages/Projects/chat-real-time';

function App() {

  const [theme, setTheme] = userPersistedState('theme', light);
  const [sideBarToggle, setSideBarToggle] = useState(false);

  const toggleTheme = () => {
    setTheme(theme.title === 'light' ? dark : light);
  };

  const toggleSideBar = () => {
    setSideBarToggle(!sideBarToggle);
  }

  return (
    <ThemeProvider theme={theme}>
      <GlobalStyle />
      <Header toggleTheme={toggleTheme} toggleSideBar={toggleSideBar} />
      <SideBar toggleSideBar={toggleSideBar} isOpen={sideBarToggle} />

      <Switch>
        <Route exact path="/" component={Home} />
        <Route exact path="/contact" component={Contact} />
        <Route exact path="/curriculum" component={Curriculum} />
        <Route exact path="/projects" component={Projects} />
        <Route exact path="/projects/chat" component={Chat} />
        <Route exact path="/techs" component={Techs} />
      </Switch>

    </ThemeProvider>
  );
}

export default App;
