import React, { useState, useEffect, useRef, useMemo } from 'react';

import socket from "../../../services/socket";

import { Container, MessageContainer, Message, MessageUser, MessageText, MessageDate, InputGroup, InputText, LabelInput, InputMessage, SendButtonInput } from './styles';

function Chat() {
    const [messages, setMessages] = useState([]);
    const [recivedMessage, setRecivedMessage] = useState(null);

    const messageText = useRef();
    const username = useRef();

    useMemo(() => { 
        socket.on('recivedMessage', message => {
            setRecivedMessage(message);
            console.log(message);
        })
    }, []);

    useEffect(()=> {
        if(recivedMessage) setMessages([...messages, recivedMessage]);
    },[recivedMessage]);

    function handleSubmit(event) {
        event.preventDefault();

        const date = new Date().toTimeString().replace(/.*(\d{2}:\d{2}:\d{2}).*/, "$1");;

        const newMessage = {
            from: 'my',
            id: 0,
            username: username.current.value,
            messageText: messageText.current.value,
            date,
        }

        socket.emit('sendMessage', newMessage);

        setMessages([...messages, newMessage]);

        messageText.current.value = '';
    }

    return (
        <Container autoComplete="off" onSubmit={handleSubmit}>

            <InputGroup>
                <LabelInput htmlFor="username">Username</LabelInput>

                <InputText
                    id="username"
                    ref={username}
                    required />
            </InputGroup>

            <MessageContainer id="messageContainer">

                {messages.map(m => (
                    <Message className={m.from} key={m.id}>
                        <MessageText>
                            <MessageUser>{m.username}</MessageUser>
                            {m.messageText}
                        </MessageText>
                        <MessageDate>{m.date}</MessageDate>
                    </Message>
                ))}

            </MessageContainer>

            <InputGroup>
                <InputMessage
                    id="message"
                    placeholder="Enviar mensagem..."
                    ref={messageText}
                    required />

                <SendButtonInput>Enviar</SendButtonInput>
            </InputGroup>

        </Container>
    );
}

export default Chat;
