import React, { useContext } from 'react';
import { Link } from 'react-router-dom';

import Switch from 'react-switch';
import { ThemeContext } from 'styled-components';
import { shade, lighten } from 'polished';

import { Container, Logo, Name, SocialMedias, Spacer } from './styles';

import Menu from '../Buttons/Menu';
import GithubButton from '../Buttons/GitHub';
import InstagramButton from '../Buttons/Instagram';
import LinkedinButton from '../Buttons/Linkedin';
import WhatsappButton from '../Buttons/Whatsapp';

function Header({ toggleTheme, toggleSideBar }) {

    function newTab(url) {
        window.open(url, '_blank');
    }

    const { colors, title } = useContext(ThemeContext);

    return (
        <>
            <Container>
                <Menu onClick={toggleSideBar} /> <Name>Bryan Diniz</Name>

                <SocialMedias>
                    <a onClick={() => newTab('https://github.com/obraia')}><GithubButton color={colors.text} /></a>
                    <a onClick={() => newTab('https://www.instagram.com/bryandinizr/')}><InstagramButton color={colors.text} /></a>
                    <a onClick={() => newTab('https://www.linkedin.com/in/bryan-diniz/')}><LinkedinButton color={colors.text} /></a>
                    <a onClick={() => newTab('https://api.whatsapp.com/send?phone=5531971594525&text=')}><WhatsappButton color={colors.text} /></a>
                </SocialMedias>

                <Switch
                    onChange={toggleTheme}
                    checked={title === 'dark'}
                    checkedIcon={false}
                    uncheckedIcon={false}
                    height={10}
                    width={35}
                    handleDiameter={20}
                    offColor={shade(0.15, colors.primary)}
                    onColor={shade(0.15, colors.primary)}
                    onHandleColor={lighten(0.1, colors.primary)}
                    offHandleColor={lighten(0.1, colors.primary)}
                />

            </Container>

            <Spacer />
        </>

    );

}

export default Header;
