import styled from "styled-components";

export const Container = styled.div`

    display: flex;
    align-items: center;

    height: 50px;
    padding: 0 30px;

    background: ${props => props.theme.colors.primary};
    color: #fff;

`;

export const Logo = styled.div`

    display: flex;
    align-items: center;

    height: 30px;
    width: 30px;
    border-radius: 15px;
    background: ${props => props.theme.colors.secondary};

`;
