import React, { useState, useContext } from 'react';

import { ThemeContext } from 'styled-components';
import { Container, ToggleButton } from './styles';

import ButtonContact from '../Buttons/Contact';
import ButtonProjects from '../Buttons/Projects';

function SideBar({ toggleSideBar, isOpen }) {

    const { colors } = useContext(ThemeContext);

    if (!isOpen) {
        return (
            <Container id={'sidebar'} style={{ width: 60 }}>
                <ToggleButton onClick={toggleSideBar} />
                <a href={'/contato'}><ButtonContact color={colors.text} /></a>
                <a href={'/projetos'}><ButtonProjects color={colors.text} /></a>
            </Container>
        );
    } else {
        return (
            <Container onDrag={toggleSideBar} id={'sidebar'} style={{ width: 110 }}>
                <ToggleButton onFocus={toggleSideBar} onBlur={toggleSideBar}/>
                <a href={'/contato'}><h4>CONTATO</h4></a>
                <a href={'/projetos'}><h4>PROJETOS</h4></a>
            </Container>
        );
    }

}

export default SideBar;
