import React, { useState, useContext, useEffect } from 'react';
import { ThemeContext } from 'styled-components';
import api from '../../services/api';
import MaskedInput from 'react-text-mask';
import CircularProgress from '@material-ui/core/CircularProgress';

import ReCAPTCHA from "react-google-recaptcha";

import {
    Container, Title, InputGroup, InputText, LabelInput, InputTextBox, Button
} from './styles';


function Contact() {

    const { title } = useContext(ThemeContext);
    const [progressbarVisible, setProgressbarVisible] = useState(false);
    const [validateCaptcha, setValidateCaptcha] = useState(false);

    let recaptchaInstance;

    const resetRecaptcha = () => {
        console.log(recaptchaInstance.props.theme);
        recaptchaInstance.reset();
      };

    async function handdleSubmit(event) {
        event.preventDefault();

        setProgressbarVisible(true);

        const data = {
            name: event.target.name.value,
            phone: event.target.phone.value,
            email: event.target.email.value,
            subject: event.target.subject.value,
            text: event.target.text.value,
        }

        const response = await api.post('/email', data);

        if (response.status == 200) {

        }

        setProgressbarVisible(false);
    }

    return (
        <Container onSubmit={handdleSubmit}>

            <Title>Contato</Title>

            <InputGroup>
                <LabelInput htmlFor="name">Nome</LabelInput>

                <InputText
                    id="name"
                    name="name"
                    onChange={resetRecaptcha}
                    required />
            </InputGroup>

            <InputGroup>
                <LabelInput htmlFor={'phone'}>Telefone</LabelInput>

                <MaskedInput
                    className="MaskedInput"
                    id="phone"
                    id="phone"
                    mask={['(', /[1-9]/, /\d/, ')', ' ', /\d/, ' ', /\d/, /\d/, /\d/, /\d/, '-', /\d/, /\d/, /\d/, /\d/]}
                    placehoder="(00) 0 0000-0000"
                    guide={true} />
            </InputGroup>

            <InputGroup>
                <LabelInput htmlFor="email">E-mail</LabelInput>

                <InputText
                    id="email"
                    name="email"
                    type="email"
                    required />
            </InputGroup>

            <InputGroup>
                <LabelInput htmlFor="subject">Assunto</LabelInput>

                <InputText
                    id="subject"
                    name="subject"
                    required />
            </InputGroup>

            <InputTextBox
                name="text"
                required />

            {progressbarVisible ? <CircularProgress style={{ marginBottom: 15 }} /> : null}

            {!validateCaptcha ?

                <ReCAPTCHA
                    sitekey="6Ld5odwUAAAAALZSMRJ1wKE3vszFTFocoP3pC-WB"
                    onChange={() => setValidateCaptcha(true)}
                    ref={e => recaptchaInstance = e}
                    theme={ title }
                    style={{ marginBottom: 15 }} /> : <Button>Enviar</Button>}

        </Container>
    );
}

export default Contact;
