import React, { useState, useContext, useRef } from 'react';

import api from '../../services/api';

import InputMask from "react-input-mask"

import { ThemeContext } from 'styled-components';
import CircularProgress from '@material-ui/core/CircularProgress';
import ReCAPTCHA from 'react-google-recaptcha';

import SuccessMessage from '../Messages/Success';
import ErrorMessage from '../Messages/Error';

import { Container, Title, InputGroup, InputText, LabelInput, InputTextBox, Button, Confirmed } from './styles';

function Contact() {

    const { title } = useContext(ThemeContext);
    const [progressbarVisible, setProgressbarVisible] = useState(false);
    const [validateCaptcha, setValidateCaptcha] = useState(false);
    const [successMessageVisible, setSuccessMessageVisible] = useState(false);
    const [errorMessageVisible, setErrorMessageVisible] = useState(false);

    const name = useRef(null);
    const phone = useRef(null);
    const email = useRef(null);
    const subject = useRef(null);
    const text = useRef(null);

    async function handleSubmit(event) {
        event.preventDefault();

        setSuccessMessageVisible(false);
        setErrorMessageVisible(false);
        setProgressbarVisible(true);

        const data = {
            name: name.current.value,
            phone: phone.current.value,
            email: email.current.value,
            subject: subject.current.value,
            text: text.current.value,
        }

        try {
            const response = await api.post('/email', data);

            if (response.status == 200) {

                name.current.value = null;
                phone.current.value = null;
                email.current.value = null;
                subject.current.value = null;
                text.current.value = null;

                setValidateCaptcha(false);
                setSuccessMessageVisible(true);
            }
            else setErrorMessageVisible(true);

        } catch (error) {
            setErrorMessageVisible(true);
        }

        setProgressbarVisible(false);
    }

    return (
        <Container onSubmit={handleSubmit}>

            <Title>Contato</Title>

            <InputGroup>
                <LabelInput htmlFor="name">Nome</LabelInput>

                <InputText
                    id="name"
                    ref={name}
                    required />
            </InputGroup>

            <InputGroup>
                <LabelInput htmlFor={'phone'}>Telefone</LabelInput>

                <InputMask
                    mask="(99) 9 9999-9999"
                    id="phone"
                    className="MaskedInput"
                    ref={phone} />
            </InputGroup>

            <InputGroup>
                <LabelInput htmlFor="email">E-mail</LabelInput>

                <InputText
                    id="email"
                    type="email"
                    ref={email}
                    required />
            </InputGroup>

            <InputGroup>
                <LabelInput htmlFor="subject">Assunto</LabelInput>

                <InputText
                    id="subject"
                    ref={subject}
                    required />
            </InputGroup>

            <InputTextBox
                ref={text}
                required />

            {progressbarVisible ? <CircularProgress style={{ marginBottom: 15 }} /> : null}

            {!validateCaptcha ?

                <ReCAPTCHA
                    sitekey="6Ld5odwUAAAAALZSMRJ1wKE3vszFTFocoP3pC-WB"
                    onChange={() => setValidateCaptcha(true)}
                    theme={title} /> : <Button>Enviar</Button>}

            {successMessageVisible ? <SuccessMessage message={'Email enviado com sucesso!'} style={{ marginBottom: 15 }} /> : null}
            {errorMessageVisible ? <ErrorMessage message={'Erro ao enviar o email.'} style={{ marginBottom: 15 }} /> : null}

        </Container>
    );
}

export default Contact;
