import React, { useContext } from 'react';
import MaskedInput from 'react-text-mask';

import {
    Container, Title, InputGroup, InputText, LabelInput, InputTextBox, Button
} from './styles';


function Contact() {

    async function handdleSubmit(e){
        e.preventDefault();

        console.log(e);
    }

    return (
        <Container onSubmit={handdleSubmit}>

            <Title>Contato</Title>

            <InputGroup>
                <LabelInput htmlFor={'name'}>Nome</LabelInput>
                <InputText id={'name'} name={'name'} required />
            </InputGroup>

            <InputGroup>
                <LabelInput htmlFor={'phone'}>Telefone</LabelInput>
                <MaskedInput
                    className={'MaskedInput'}
                    id={'phone'}
                    name={'phone'} 
                    mask={['(', /[1-9]/, /\d/, ')', ' ', /\d/, ' ', /\d/, /\d/, /\d/, /\d/, '-', /\d/, /\d/, /\d/, /\d/]}
                    placehoder="(00) 0 0000-0000"
                    guide={true}
                    required />
            </InputGroup>

            <InputGroup>
                <LabelInput htmlFor={'email'}>E-mail</LabelInput>
                <InputText id={'email'} name={'email'} required />
            </InputGroup>

            <InputTextBox id={'subject'} name={'subject'} required />

            <Button>Enviar</Button>

        </Container>
    );
}

export default Contact;
