import React, { useState, useContext } from 'react';

import { ThemeContext } from 'styled-components';
import { Container, ToggleButtonClosed, ArrowRight, ToggleButtonOpened, ArrowLeft } from './styles';

import ButtonContact from '../Buttons/Contact';
import ButtonProjects from '../Buttons/Projects';

function SideBar({ toggleSideBar, isOpen }) {

    const { colors } = useContext(ThemeContext);

    if (!isOpen) {
        return (
            <Container id={'sidebar'} style={{ width: 60 }}>
                <ToggleButtonClosed onClick={toggleSideBar}>
                    <ArrowRight />
                </ToggleButtonClosed>
                <a href={'/contact'}><ButtonContact color={colors.text} /></a>
                <a href={'/project'}><ButtonProjects color={colors.text} /></a>
            </Container>
        );
    } else {
        return (
            <Container id={'sidebar'} style={{ width: 110  }}>
                <ToggleButtonOpened onClick={toggleSideBar}>
                    <ArrowLeft />
                </ToggleButtonOpened>
                <a href={'/contact'}><h4>CONTATO</h4></a>
                <a href={'/project'}><h4>PROJETOS</h4></a>
            </Container>
        );
    }

}

export default SideBar;
