import React, { useState, useContext } from 'react';

import { ThemeContext } from 'styled-components';
import { Container, ToggleButtonClosed, ArrowRight, ToggleButtonOpened, ArrowLeft } from './styles';

import ButtonHome from '../Buttons/Home';
import ButtonContact from '../Buttons/Contact';
import ButtonProjects from '../Buttons/Projects';
import ButtonTechs from '../Buttons/Techs';

function SideBar({ toggleSideBar, isOpen }) {

    const { colors } = useContext(ThemeContext);

    if (!isOpen) {
        return (
            /*

            <Container id={'sidebar'} style={{ width: 60 }}>
                <ToggleButtonClosed onClick={toggleSideBar}>
                    <ArrowRight />
                </ToggleButtonClosed>
                <a href={'/'}><ButtonHome color={colors.text} /></a>
                <a href={'/contact'}><ButtonContact color={colors.text} /></a>
                <a href={'/project'}><ButtonProjects color={colors.text} /></a>
                <a href={'/techs'}><ButtonTechs color={colors.text} /></a>
            </Container>

            */


            <Container id={'sidebar'} style={{ width: 60 }}>
                <ToggleButtonOpened onClick={toggleSideBar}>
                    <ArrowLeft />
                </ToggleButtonOpened>
                <a href={'/'}><h4><ButtonHome color={colors.text} />&nbsp;HOME</h4></a>
                <a href={'/contact'}><h4><ButtonContact color={colors.text} />&nbsp;CONTATO</h4></a>
                <a href={'/project'}><h4><ButtonProjects color={colors.text} />&nbsp;PROJETOS</h4></a>
                <a href={'/techs'}><h4><ButtonTechs color={colors.text} />&nbsp;TECHS</h4></a>
            </Container>
        );
    } else {
        return (
            <Container id={'sidebar'} style={{ width: 145, left: 0 }}>
                <ToggleButtonOpened onClick={toggleSideBar}>
                    <ArrowLeft />
                </ToggleButtonOpened>
                <a href={'/'}><h4><ButtonHome color={colors.text} />&nbsp;HOME</h4></a>
                <a href={'/contact'}><h4><ButtonContact color={colors.text} />&nbsp;CONTATO</h4></a>
                <a href={'/project'}><h4><ButtonProjects color={colors.text} />&nbsp;PROJETOS</h4></a>
                <a href={'/techs'}><h4><ButtonTechs color={colors.text} />&nbsp;TECHS</h4></a>
            </Container>
        );
    }
}

export default SideBar;
